% mnnysfreq.m: calculate mean frequency and period of nystagmus cycles from zoomtool.% Usage: In zoomtool:	1) clear both cursors (C1 Clear and C2 Clear)%					 	2) place C1 at the start of one and C2 at the start of the next cycle%						3) click on C1 xy and C2 xy%					 	4) repeat 2) and 3) for all nystagmus cycles in interval%		 In Command Window:	5) mnnysfreq%% Written by:  L.F. Dell'Osso%              February 2004 (last mod: 2/29/04)xdiff=abs(xyCur2Mat(:,1)-xyCur1Mat(:,1));mnper=1000*mean(xdiff)/samp_freq;minper=1000*min(xdiff)/samp_freq;maxper=1000*max(xdiff)/samp_freq;mnfreq=1000/mnper;minfreq=1000/maxper;maxfreq=1000/minper;numcy=size(xdiff);disp([' Mean Nystagmus Frequency = ' num2str(mnfreq,2) ' Hz'])disp([' Mean Nystagmus Period = ' num2str(mnper) ' msec'])disp([' Number of Nystagmus Cycles = ' num2str(numcy(:,1)) ' '])disp([' Min Freq <=> Max Period = ' num2str(minfreq,2) ' Hz <=> ' num2str(round(maxper)) ' msec'])disp([' Max Freq <=> Min Period = ' num2str(maxfreq,2) ' Hz <=> ' num2str(round(minper)) ' msec'])