% NAFXestPlot.m% m-file to plot Estimated NAFX Improvement vs. Pre-Op NAFX% using the function nafxest.m% Written by LF Dell'Osso on 4/9/09% Last modified: 4/9/09 by LF Dell'Osso% Repaired by Jonathan Jacobs 6 Dec 2017function nafxestplotfigure; hold on; box% fplot( @(x) nafxest(x,1),[0 .6],'color','g');% fplot( @(x) nafxest(x,1),[.6 .65],'color','y');% fplot( @(x) nafxest(x,1),[.65 .99],'color','r');cnt=0;x=[];for i=0.0:0.01:0.6   cnt=cnt+1;   x(cnt)=i;   a(cnt)=nafxest(i,1);endplot(x,a,'g')cnt=0;x=[];for j=0.61:0.01:0.65   cnt=cnt+1;   x(cnt)=j;   b(cnt)=nafxest(j,1);endplot(x,b,'y')cnt=0;x=[];for k=0.66:0.01:0.99   cnt=cnt+1;   x(cnt)=k;   c(cnt)=nafxest(k,1);endplot(x,c,'r')xlabel('Pre-Tenotomy NAFX')ylabel('NAFX Increase (%)')title('Percent Increase in Peak NAFX')