%clear all    % if you feel like itfunction eb3dtestgen(temp)% these must be declared global in the base workspaceglobal lh rh lv rv lt rt samp_freqevalin('base', 'global lh rh lv rv lt rt samp_freq')if nargin == 0   samp_freq = 200; else   samp_freq = temp;endf_cn = 3;         % CN fundamental frequency of 3 Hzdur = 5;          % 5 seconds of datavect=1/samp_freq:1/samp_freq:dur;vect = vect';t=maket(vect, samp_freq);% OD & OS will be conjugate hor hor, vrt & tor datalh = 10*sin(2*pi*f_cn*vect); rh=lh;lv =  5*sin(2*pi*f_cn*vect); rv=lv; lt =  2*sin(2*pi*f_cn*vect); rt=lt;