function plthvglobal rh rv lh lv st sv datanameplthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';end%lvColor = [.7 .5 0];lvColor = lhColor;rvColor = rhColor;fontsize=12;novrt=0; %#ok<NASGU>if ~isempty(rv)   t=maket(rv); elseif ~isempty(lv)   t=maket(lv); else   disp('No vertical data!?!')   plth   returnendmaxt=max(t);hold onif ~isempty(lh)   lhH=plot(t,lh,lhColor);   lhH.DisplayName='lh';   lhtxtH=text(maxt,lh(end),' LH');   lhtxtH.Color=lhColor;   lhtxtH.FontSize=fontsize;end   if ~isempty(rh)   rhH=plot(t,rh,rhColor);   rhH.DisplayName='rh';   rhtxtH=text(maxt,rh(end),' RH');   rhtxtH.Color=rhColor;   rhtxtH.FontSize=fontsize;end   if ~isempty(st)   stH=plot(t,st,'r');   stH.DisplayName='st';   sttxtH=text(maxt,st(end),' HStm');   sttxtH.FontSize=fontsize;   sttxtH.Color='r';end  if ~isempty(lv)   lvH=plot(t,lv,lvColor);   lvH.DisplayName='lv';   lvtxtH=text(maxt,lv(end),' LV');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;end   if ~isempty(rv)   rvH=plot(t,rv,rvColor);   rvH.DisplayName='rv';   rvtxtH=text(maxt,rv(end),' RV');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;end   if ~isempty(sv)   svH=plot(t,sv,'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(end),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endend   ept;