function pltvglobal rv lv st datanameplthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lvColor = 'g';   rvColor = 'b'; elseif plthFigColor(1) < 0.6;   lvColor = 'y';   rvColor = 'c';endif ~isempty(rv)   t=maket(rv); elseif ~isempty(lv)   t=maket(lv); else   disp('No vertical data!?!')   returnend maxpt=max(max([lv,rv]));minpt=min(min([lv,rv]));maxt=max(t);hold onif ~isempty(lv)   plot(t,lv,lvColor)   levH=text(maxt,minpt,'LEV');   set(levH,'color', lvColor);end   if ~isempty(rv)   plot(t,rv,rvColor)   revH=text(maxt,maxpt,'REV');   set(revH,'color', rvColor);end   if ~isempty(st)   plot(t,st,'r')   stH=text(maxt,maxpt,'Stim');   set(stH,'color', 'r');end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendept; dragger%clear reH leH plthFigH plthFigColor lvColor