function pltvglobal rv lv sv datanameplthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lvColor = 'g';   rvColor = 'b'; elseif plthFigColor(1) < 0.6   lvColor = 'y';   rvColor = 'c';endif ~isempty(rv)   t=maket(rv); elseif ~isempty(lv)   t=maket(lv); else   disp('No vertical data!?!')   returnendmaxt=max(t);fontsize=12;hold onif ~isempty(lv)   lvH=plot(t,lv,lvColor);   lvH.DisplayName='lv';   lvtxtH=text(maxt,lv(end),' LV');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;endif ~isempty(rv)   rvH=plot(t,rv,rvColor);   rvH.DisplayName='rv';   rvtxtH=text(maxt,rv(end),'RV');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;endif ~isempty(sv)   svH=plot(t,sv,'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(end),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;endif exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendevt;