% omstart.m:   execute when MATLAB starts up.%                1) Run 'OMpath' to add OMtools directories to path%                2) Move to OMtools main directory%% This file should be in the same folder as findomtools.m, setomtools.m,% and ompath.m.% Written by:  Jonathan Jacobs%              March 2017  (last mod: 03/09/17)function omstart( ~ )omtoolspath = findomtools;if isempty(omtoolspath)   disp('No OMtools installation found.')   disp('Try running install_omtools.')   returnendsetomtools(omtoolspath);cd('omtoolsdirs')ompathomprefpath = findomprefs;disp(' ')disp(['OMtools preferences:    ' omprefpath])ztprefpath = findztprefs;disp(['zoomtools preferences:  ' ztprefpath])datarootpath = dataroot('r');disp(['Default data directory: ' datarootpath ...   '  --  To change, type dataroot(''w'')'])disp(' ')disp('OMtools is ready for use.')cd(datarootpath)