% sub.m: Take a subsection of an array.% Usage: subArray = sub(array, startTime, duration);% See also: SUBP, TPLOT% Written by:  Jonathan Jacobs%              February 1997 - March 1997  (last mod: 03/02/97)function subArray = sub(inArray, startTime, duration, samp_freq)if nargin<4   global samp_freq %#ok<TLEV,*REDEF>endif isempty(samp_freq) %#ok<*NODEF>   samp_freq = input('Enter the sampling frequency: '); %#ok<*NASGU>endif nargin == 0   help sub   returnend[numDRows,numDCols] = size( inArray );if (numDRows < numDCols)   inArray = inArray';   [numDRows,numDCols] = size( inArray ); %#ok<ASGLU>endstartPt = startTime*samp_freq;if startPt==0, startPt=1; endstopPt = startPt + duration*samp_freq - 1;if stopPt>numDRows, stopPt=numDRows; endstartPt = fix(startPt);stopPt  = fix(stopPt);subArray = inArray(startPt:stopPt, : );