% tts.m: Convert the time axis from Time To Samples% Usage:  tts( sampFreq )  % Written by:  Jonathan Jacobs%              April 1998 (last mod:  04/03/98)function tts(inpSF)global samp_freqif nargin==0 || isempty( inpSF )   samp_freq = input('Enter the sampling frequency: ');else   samp_freq = inpSF;endga = gca;gf = gcf;if strcmp(get(gf,'Tag'), 'zoomed window')   disp('tts can''t operate while ZOOMTOOL is active')   returnendtemp = get(ga,'Children');count=1;linelist=nan(1,length(temp));for i=1:length(temp)   if strcmp(get(temp(i),'Type'),'line')      linelist(count)=temp(i);      count = count+1;   endendminX=1000000;maxX=-1000000;for i=1:length(linelist)   x=get(linelist(i),'XData');   x=x*samp_freq;   minX=min(minX,min(x));   maxX=max(maxX,max(x));   set(linelist(i),'XData',x);endset(gca,'XLim', [minX maxX]);