% refresh.m:  redraw the screen% this works better than built-in 'refresh'% Written by Jonathan Jacobs%            February 1997   xlims = get(gca, 'Xlim');   xlo = xlims(1);   xhi = xlims(2);   if xhi > 0      set(gca, 'Xlim', [0 xhi]);    elseif xhi < 0      set(gca, 'Xlim', [xhi 0]);    elseif xhi == 0      set(gca, 'Xlim', [xlo 0]);   end   set(gca, 'Xlim', xlims);