% cur_emode.m: set the erase mode for the lines of% the cursors.% Written by:  Jonathan Jacobs% July 2001 (last mod: 07/02/01)function cur_emode% get handles stored in Axis 'UserData'temp = get(gca,'UserData');     h2 = temp{2};cur1horH=h2{1};     cur1vertH=h2{4};    cur2horH=h2{7};cur2vertH=h2{10};   hCurMode=h2{19};val = get(hCurMode,'value');str = get(hCurMode,'string');tmp_emode = str(val,:);set(cur1horH,'erasemode',tmp_emode);set(cur1vertH,'erasemode',tmp_emode);set(cur2horH,'erasemode',tmp_emode);set(cur2vertH,'erasemode',tmp_emode);% saved cursor erase modecurdir=pwd;%eval(['cd ' '''' matlabroot ''''])cd(findztprefs)zoom_fig_size = get(gca,'Position');emode = tmp_emode;save ztPrefs.mat emode zoom_fig_sizeeval(['cd ' '''' curdir ''''])