% curcolor.m:  Change the cursor colors% Written by:  Jonathan Jacobs%              January 1998  (last mod: 01/13/98)function curcolor(which)temp = get(gca,'USerData');h2 = temp{2};cur1LblX=h2{2};       cur1LblY=h2{3};cur2LblX=h2{8};       cur2LblY=h2{9};hCur1Box=h2{17};      hCur2Box=h2{18};hCur1Frame=h2{39};	  hCur2Frame=h2{40};cur1Col = get(cur1LblX,'BackGroundColor');cur2Col = get(cur2LblX,'BackGroundColor');newcolor = uisetcolor;if newcolor == 0   returnendif strcmp(which, 'curOne')   h = findline(gca,1001);   set(h,'Color',newcolor);   h = findline(gca,1002);   set(h,'Color',newcolor);   set(cur1LblX,'BackGroundColor', newcolor);   set(cur1LblY,'BackGroundColor', newcolor);   set(hCur1Box,'BackGroundColor', newcolor);   % workaround for ML bug 377211   set(hCur1Frame,'BackGroundColor', newcolor);   cur1Col = newcolor; elseif strcmp(which, 'curTwo')   h = findline(gca,2001);   set(h,'Color',newcolor);   h = findline(gca,2002);   set(h,'Color',newcolor);   set(cur2LblX,'BackGroundColor', newcolor);   set(cur2LblY,'BackGroundColor', newcolor);   set(hCur2Box,'BackGroundColor', newcolor);   set(hCur2Frame,'BackGroundColor', newcolor);   cur2Col = newcolor;endcurdir=pwd;%cd(matlabroot)cd(findztprefs)eval(['save curscolr.mat cur1Col cur2Col'])eval('cd(curdir)','')