% rbzoom.m: checks state of button, changes text when toggled,% turns 'zoom' on or off, and calls 'autorange_y, newlims' to% update when the zoom is done% Written by:  Jonathan Jacobs% October 2003 - February 2010 (last mod: 02/09/10)function rbzoom(cmd)state = get(gco,'Value');switch lower(cmd)   case {'zoom'}		switch state		  case 0			set(gco,'String','is OFF');			zoom off			newlims     					  case 1			set(gco,'String','is ON');			xlims=get(gca,'XLim');			ylims=get(gca,'YLim');			set(gco,'UserData',[xlims ylims])			zoom reset			zoom on			newlims		end	case {'reset'}			hRBzoom = get(gco,'UserData');		axlims = get(hRBzoom,'UserData');		if isempty(axlims)			return		end		set(gca,'XLim',[axlims(1) axlims(2)])		set(gca,'YLim',[axlims(3) axlims(4)])		newlimsend