serData',gca);cur1LblY = uicontrol('Style','text','Units','normal',...   'Position',[rvbase+1*b_width tbase b_width*.9 b_hite],...   'ForeGroundColor',color1.bgstr,...   'BackGroundColor',cur1Col,...   'FontSize', fixfontsize, ...   'String','-- Y --');handles(11) = uicontrol('Style','edit','Units','normal',...   'Position',[rvbase+4*b_width hbase b_width*.9 b_hite],...   'String',num2str(abs(v2-v1)),'UserData',gca,'Value',3003,...   'FontSize', edfontsize, ...   'BackGroundColor','cyan','ForeGroundColor','black',...   'Callback','zoomset(get(get(gcf,''CurrentObject''),''UserData''),3);');uicontrol('Style','text','Units','normal',...   'Position',[rvbase+4*b_width tbase b_width*.9 b_hite],...   'BackGroundColor',[0.75 0.75 0.75],...   'ForeGroundColor','black',...   'FontSize', fixfontsize, ...   'String','Delta X');handles(12) = uicontrol('Style','text','Units','normal',...   'Position',[rvbase+5*b_width hbase b_width*.9 b_hite],...   'BackGroundColor',[0.75 0.75 0.75],...   'ForeGroundColor','black',...   'FontSize', fixfontsize, ...   'String',num2str(abs(h2-h1)),'UserData',gca);uicontrol('Style','text','Units','normal',...   'Position',[rvbase+5*b_width tbase b_width*.9 b_hite],...   'BackGroundColor',[0.75 0.75 0.75],...   'ForeGroundColor','black',...   'FontSize', fixfontsize, ...   'String','Delta Y');handles(13) = uicontrol('Style','edit','Units','normal',...   'Position',[rvbase+2*b_width hbase b_width*.9 b_hite],...   'String',num2str(v2),'UserData',gca,'Value',3002,...   'FontSize', edfontsize, ...   'BackGroundColor','cyan','ForeGroundColor','black',...   'Callback','zoomset(get(get(gcf,''CurrentObject''),''UserData''),2);');cur2LblX = uicontrol('Style','text','Units','normal',...   'Position',[rvbase+2*b_width tbase b_width*.9 b_hite],...   'BackGroundColor',cur2Col,...   'ForeGroundColor',color2.bgstr,...   'FontSize', fixfontsize, ...   'String','-- X --');handles(14) = uicontrol('Style','text','Units','normal',...   'Position',[rvbase+3*b_width hbase b_width*.9 b_hite],...   'BackGroundColor',[0.75 0.75 0.75],...   'ForeGroundColor','black',...   'FontSize', fixfontsize, ...   'String',num2str(h2),'UserData',gca);cur2LblY = uicontrol('Style','text','Units','normal',...   'Position',[rvbase+3*b_width tbase b_width*.9 b_hite],...   'ForeGroundColor',color2.bgstr,...   'BackGroundColor',cur2Col,...   'FontSize', fixfontsize, ...   'String','-- Y --');% put all the important handles into the axis UserData so we can access% their respective controls from subfunctions w/o having to make globals% there are 23 'handles()' entries and 40 'myHandles()' entries.% added by Jonathan JacobsmyHandles = ...   {cur1horH        cur1LblX         cur1LblY         cur1vertH,...   cur1VisFrameBgH  cur1VisFrameH    cur2horH         cur2LblX,...   cur2LblY         cur2vertH        cur2VisFrameBgH  cur2VisFrameH,...   hC1Color         hC2Color         hClrX            hClrXY,...   hCur1Box         hCur2Box         hCurMode         hLToggle,...   hXHigh           hXLow            hYAutoRange      hYHigh,...   hYLow            lhand            linVisFrameBgH   linVisFrameH,...   oldBGcolor       oldWindNextPlot  oldWindTag       ztOrigXLims,...   ztOrigYLims      ztUpdateH        init_fig_size	   init_fig_units ...   hRBzoom          init_ax_pos	    hCur1Frame		   hCur2Frame...   hLToggleFrame    hRBreset         hCur1Width       hCur2Width...   };handleList  = {handles; myHandles; lhand};gf.UserData = lhand;%handleList = {handles; myHandles};ga.UserData = handleList;curwidth('curOne')curwidth('curTwo')% activate the Y-autorangeif hYAutoRange.Value   autorange_yendgf.CurrentAxes=ga;if nargout==1   ztw=gf;end